mapper.select:
SELECT u.*, d.d_name
FROM user u left join department d ON u.major_id=d.id
WHERE type=2;

SELECT u.*, d.d_name
FROM user u left join department d ON u.major_id=d.id
WHERE type=3;

SELECT u.*, d.d_name
FROM user u left join department d ON u.major_id=d.id
WHERE type=4;

SELECT u.*, d.d_name
FROM user u left join department d ON u.major_id=d.id
WHERE name like '%'||#{name}||'%';
